mapper.insert:
INSERT INTO COMMENT(
      BOARD_NUM
    , MEM_ID
    , CONTENT
    , PARENT_COMMENT_NUM
)VALUES(
      #{boardNum}
    , #{memId}
    , #{content}
    , #{parentCommentNum}
)

mapper.select:
SELECT COUNT(*)
FROM COMMENT
WHERE BOARD_NUM = #{boardNum}

mapper.update:
UPDATE COMMENT
SET CONTENT = #{content}
WHERE COMMENT_NUM = #{commentNum}

mapper.delete:
DELETE FROM COMMENT
WHERE COMMENT_NUM = #{commentNum}
   OR PARENT_COMMENT_NUM = #{commentNum}
